const moment = require('moment');
const tenantId = $('Config Tenant').first().json.tenant_id;
const inicioExecucao = $('Config Tenant').first().json.inicio_execucao;
const configs = $('Buscar Config').all();
const palavras = $('Buscar Palavras').all();

const palavrasInclusao = palavras.filter(p => p.json.tipo === 'INCLUSAO').map(p => p.json.palavra);
const palavrasExclusao = palavras.filter(p => p.json.tipo === 'EXCLUSAO').map(p => [p.json.palavra, ...(p.json.variacoes || [])]).flat();

const buscas = [];
for (const config of configs) {
  const dias = config.json.dias_retroativos || 8;
  const dataInicial = moment().subtract(dias, 'days').format('YYYYMMDD');
  const dataFinal = moment().format('YYYYMMDD');
  // PNCP: 6 = Pregão Eletrônico, 8 = Dispensa (default de fallback)
  const mods = config.json.modalidades_contratacao || [6, 8];

  // Tratar UF: Se for nulo ou array vazio, usa null para buscar tudo
  let ufs = config.json.ufs;
  if (!ufs || ufs.length === 0) {
    ufs = [null]; 
  }

  for (const mod of mods) {
    for (const uf of ufs) {
      buscas.push({ 
        tenant_id: tenantId, 
        inicio_execucao: inicioExecucao, 
        data_inicial: dataInicial, 
        data_final: dataFinal, 
        modalidade: mod, 
        uf: uf, // Pode ser 'MG', 'SP' ou null/undefined
        palavras_inclusao: palavrasInclusao,
        palavras_exclusao: palavrasExclusao, 
        tenant_config: config.json.tenant_config || {} 
      });
    }
  }
}
return buscas.map(b => ({ json: b }));

