const moment = require('moment');
const licitacoesFiltradas = [];

// Helper para normalizar texto (remove acentos e lowercase)
const normalize = (text) => (text || '').toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, "");

// Situações válidas (1 = Divulgada no PNCP)
const SITUACOES_VALIDAS = [1];

// Função híbrida de busca - campos específicos + fallback JSON
const buscarPalavraHibrido = (licitacao, palavra) => {
  const palavraNorm = normalize(palavra);

  // 1) Campos prioritários (mais relevantes)
  const camposPrioritarios = [
    licitacao.objetoCompra,
    licitacao.informacaoComplementar,
    licitacao.orgaoEntidade?.razaoSocial,
    licitacao.unidadeOrgao?.nomeUnidade,
    licitacao.unidadeOrgao?.municipioNome
  ];

  for (const campo of camposPrioritarios) {
    if (campo && normalize(campo).includes(palavraNorm)) {
      return { encontrou: true, fonte: 'campo_prioritario' };
    }
  }

  // 2) Fallback: JSON inteiro (campos não mapeados)
  const jsonCompleto = normalize(JSON.stringify(licitacao));
  if (jsonCompleto.includes(palavraNorm)) {
    return { encontrou: true, fonte: 'json_completo' };
  }

  return { encontrou: false, fonte: null };
};

const buscaParams = $('Gerar Loop Paginas').first().json;
const palavrasInclusao = buscaParams.palavras_inclusao || [];
const palavrasExclusao = buscaParams.palavras_exclusao || [];
const tenantConfig = buscaParams.tenant_config || {};
const agora = moment();

const addSample = (arr, licitacao, extra = {}) => {
  if (arr.length >= 3) return;
  arr.push({
    numeroControlePNCP: licitacao.numeroControlePNCP,
    situacaoCompraId: licitacao.situacaoCompraId,
    situacaoCompraNome: licitacao.situacaoCompraNome,
    dataEncerramentoProposta: licitacao.dataEncerramentoProposta,
    objetoCompra: (licitacao.objetoCompra || '').slice(0, 160),
    ...extra,
  });
};

let stats = {
  paginas_recebidas: $input.all().length,
  paginas_com_data: 0,
  paginas_sem_data: 0,
  total_recebidas: 0,
  filtradas_situacao: 0,
  filtradas_data: 0,
  filtradas_data_invalida: 0,
  filtradas_palavra: 0,
  filtradas_exclusao: 0,
  aprovadas: 0,
  matches_campo_prioritario: 0,
  matches_json_completo: 0,
  matches_por_palavra: {},
  samples: {
    situacao: [],
    data: [],
    palavra: [],
    exclusao: [],
    sem_data: [],
  }
};

for (const item of $input.all()) {
  const response = item.json;

  if (!Array.isArray(response.data)) {
    stats.paginas_sem_data++;
    continue;
  }

  stats.paginas_com_data++;

  for (const licitacao of response.data) {
    stats.total_recebidas++;

    // Dados brutos
    const uf = licitacao.unidadeOrgao?.ufSigla || '';
    const valorEstimado = licitacao.valorTotalEstimado || 0;

    // Detectar tipo de participação (não filtra aqui; só anota)
    let tipoParticipacao = 'AMPLA';
    if (licitacao.srp === true) {
      tipoParticipacao = 'SRP';
    }
    if (licitacao.modalidadeNome && normalize(licitacao.modalidadeNome).includes('me/epp')) {
      tipoParticipacao = 'ME/EPP';
    }

    // Filtro 1: Situação
    if (!SITUACOES_VALIDAS.includes(licitacao.situacaoCompraId)) {
      stats.filtradas_situacao++;
      addSample(stats.samples.situacao, licitacao);
      continue;
    }

    // Filtro 2: Encerramento no futuro (aplica apenas quando h? data)
    let dataEncerramento = null;
    if (licitacao.dataEncerramentoProposta) {
      dataEncerramento = moment(licitacao.dataEncerramentoProposta);
      if (!dataEncerramento.isValid()) {
        stats.filtradas_data_invalida++;
        addSample(stats.samples.sem_data, licitacao, { motivo: 'dataEncerramentoProposta_invalida' });
        continue;
      }

      if (dataEncerramento.isBefore(agora)) {
        stats.filtradas_data++;
        addSample(stats.samples.data, licitacao);
        continue;
      }
    }

    // Filtro 3: Palavras de inclusão (OR) - híbrido
    let temPalavraInclusao = false;
    let palavraMatch = '';
    let fonteMatch = '';

    if (palavrasInclusao.length === 0) {
      temPalavraInclusao = true;
      palavraMatch = 'SEM_FILTRO';
      fonteMatch = 'sem_filtro';
    } else {
      const palavrasUnicas = [...new Set(palavrasInclusao)];
      for (const palavra of palavrasUnicas) {
        const resultado = buscarPalavraHibrido(licitacao, palavra);
        if (resultado.encontrou) {
          temPalavraInclusao = true;
          palavraMatch = palavra;
          fonteMatch = resultado.fonte;

          stats.matches_por_palavra[palavra] = (stats.matches_por_palavra[palavra] || 0) + 1;

          if (resultado.fonte === 'campo_prioritario') {
            stats.matches_campo_prioritario++;
          } else {
            stats.matches_json_completo++;
          }

          break;
        }
      }
    }

    if (!temPalavraInclusao) {
      stats.filtradas_palavra++;
      addSample(stats.samples.palavra, licitacao);
      continue;
    }

    // Filtro 4: Palavras de exclusão (OR) - híbrido
    let excluir = false;
    let motivoExclusao = null;

    const exclusaoUnicas = [...new Set(palavrasExclusao)];
    for (const excl of exclusaoUnicas) {
      const resultado = buscarPalavraHibrido(licitacao, excl);
      if (resultado.encontrou) {
        excluir = true;
        motivoExclusao = 'palavra_exclusao:' + excl + ' (fonte:' + resultado.fonte + ')';
        break;
      }
    }

    if (excluir) {
      stats.filtradas_exclusao++;
      addSample(stats.samples.exclusao, licitacao, { motivo: motivoExclusao });
      continue;
    }

    stats.aprovadas++;

    licitacoesFiltradas.push({
      numero_controle_pncp: licitacao.numeroControlePNCP,
      ano_compra: licitacao.anoCompra,
      sequencial_compra: licitacao.sequencialCompra,
      cnpj_orgao: licitacao.orgaoEntidade?.cnpj,
      orgao_nome: licitacao.orgaoEntidade?.razaoSocial,
      objeto_compra: licitacao.objetoCompra,
      valor_total_estimado: valorEstimado,
      modalidade_contratacao: licitacao.modalidadeNome,
      situacao_compra_id: licitacao.situacaoCompraId,
      situacao_compra_nome: licitacao.situacaoCompraNome,
      tipo_participacao: tipoParticipacao,
      data_publicacao: licitacao.dataPublicacaoPncp,
      data_encerramento_proposta: licitacao.dataEncerramentoProposta,
      uf: uf,
      municipio: licitacao.unidadeOrgao?.municipioNome,
      link_sistema_origem: licitacao.linkSistemaOrigem,
      passou_pre_triagem: true,
      motivo_filtro: motivoExclusao,
      tenant_id: buscaParams.tenant_id,
      palavra_match: palavraMatch,
      fonte_match: fonteMatch
    });
  }
}

console.log('[BUSCA_PNCP] Filtros (por busca)', {
  tenant_id: buscaParams.tenant_id,
  uf: buscaParams.uf,
  modalidade: buscaParams.modalidade,
  data_inicial: buscaParams.data_inicial,
  data_final: buscaParams.data_final,
  palavras_inclusao_qtd: palavrasInclusao.length,
  palavras_exclusao_qtd: palavrasExclusao.length,
  stats,
  // amostras pequenas para validar motivos de corte
  samples: stats.samples,
  // top matches por palavra
  matches_por_palavra: Object.entries(stats.matches_por_palavra)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10)
    .reduce((acc, [k, v]) => ({ ...acc, [k]: v }), {}),
});

if (licitacoesFiltradas.length === 0) {
  return [{
    json: {
      debug: true,
      stats,
      busca: {
        tenant_id: buscaParams.tenant_id,
        uf: buscaParams.uf,
        modalidade: buscaParams.modalidade,
        data_inicial: buscaParams.data_inicial,
        data_final: buscaParams.data_final,
      },
      palavras_inclusao: palavrasInclusao,
      mensagem: 'Nenhuma licitação passou nos filtros'
    }
  }];
}

return licitacoesFiltradas.map(l => ({ json: l }));

/**
 * n8n Code Node (JavaScript)
 * Entrada: vários items (ex: 700), cada um com item.json = objeto
 * Saída: 1 item por lote, contendo items[] com até 30 objetos
 */

const BATCH_SIZE = 5;

// Pega todos os objetos do fluxo (um por item do n8n)
const allObjects = $input.all().map(i => i.json);

// Chunk / split em lotes
const batches = [];
for (let i = 0; i < allObjects.length; i += BATCH_SIZE) {
  batches.push(allObjects.slice(i, i + BATCH_SIZE));
}

// Retorna 1 item por lote
return batches.map((batch, idx) => ({
  json: {
    batch_index: idx + 1,
    total_batches: batches.length,
    batch_size: BATCH_SIZE,
    items: batch, // <-- aqui estão os 30 objetos
  }
}));
